import React, { Component } from 'react'
import chart from './index.module.css'
import { message } from 'antd';
import ReactEcharts from 'echarts-for-react';
import wifi_logo from '../../../asserts/photo/curnet/wifi.png'


export default class index extends Component {

  componentDidMount(){

  }

  render() {
    return (
      <div className={chart.container}>
        <div className={chart.box}>
          <ReactEcharts option={this.getOption()} style={{width: '100%', height: '100%'}}/>
        </div>
        <div className={chart.overlay} style={{width: '50rem', height: '5rem'}}>
          <span style={{marginLeft: '1.625rem', width: '5rem', height: '5rem'}}>
            <img src={wifi_logo} style={{width: '100%', height: '100%'}}/>
          </span>
          <label style={{marginLeft: '1.625rem', color: '#333333', fontSize: '3.4rem', lineHeight: '3rem', fontWeight: 'bolde'}}>TP-LINK-1111</label>
        </div>
        <div className={chart.overlay_1}>
          <span className={chart.overlay_1_item}></span>
          <span className={chart.overlay_1_item}></span>
          <span className={chart.overlay_1_item}></span>
          <span className={chart.overlay_1_item}></span>
        </div>
      </div>
    )
  }

  getOption = () => {
    let option = {
      series: [
        {
          type: 'gauge',
          axisLine: {
            lineStyle: {
              width: 30,
              color: [
                [0.3, '#67e0e3'],
                [0.7, '#37a2da'],
                [1, '#fd666d']
              ]
            }
          },
          pointer: {
            itemStyle: {
              color: 'auto'
            }
          },
          axisTick: {
            distance: -30,
            length: 8,
            lineStyle: {
              color: '#fff',
              width: 2
            }
          },
          splitLine: {
            distance: -30,
            length: 30,
            lineStyle: {
              color: '#fff',
              width: 4
            }
          },
          axisLabel: {
            color: 'auto',
            distance: 40,
            fontSize: 20
          },
          detail: {
            valueAnimation: true,
            formatter: '{value} km/h',
            color: 'auto'
          },
          data: [
            {
              value: 70
            }
          ]
        }
      ]
    };
    return option
  }
}
