import React, { Component } from 'react'
import chart from './index.module.css'
import { message } from 'antd';
import ReactEcharts from 'echarts-for-react';


export default class index extends Component {

  componentDidMount(){

  }

  render() {
    return (
      <div className={chart.container}>
        <div className={chart.box}>
          <ReactEcharts option={this.getOption()} style={{width: '100%', height: '100%'}}/>
        </div>
        <div className={chart.overlay} style={{width: '20rem'}}>TP_LINK - 11110</div>
      </div>
    )
  }

  getOption = () => {
    let option = {
      series: [
        {
          type: 'gauge',
          axisLine: {
            lineStyle: {
              width: 30,
              color: [
                [0.3, '#67e0e3'],
                [0.7, '#37a2da'],
                [1, '#fd666d']
              ]
            }
          },
          pointer: {
            itemStyle: {
              color: 'auto'
            }
          },
          axisTick: {
            distance: -30,
            length: 8,
            lineStyle: {
              color: '#fff',
              width: 2
            }
          },
          splitLine: {
            distance: -30,
            length: 30,
            lineStyle: {
              color: '#fff',
              width: 4
            }
          },
          axisLabel: {
            color: 'auto',
            distance: 40,
            fontSize: 20
          },
          detail: {
            valueAnimation: true,
            formatter: '{value} km/h',
            color: 'auto'
          },
          data: [
            {
              value: 70
            }
          ]
        }
      ]
    };
    return option
  }
}
